import React, { Component } from "react";
//import RRule from "rrule";
import { Calendar } from "@fullcalendar/core";
import timeGridPlugin from "@fullcalendar/timegrid";
import FullCalendar from "@fullcalendar/react";

export default class CalendarDemo extends Component {
    constructor(props) {
        super(props);
        this.state = {
            calendar: null,
            events: this.props.events
        };
        this.calendarRef = React.createRef();
    }

    componentDidMount() {

        const calendar = new Calendar(this.calendarRef.current, {
            //plugins
            plugins: [ timeGridPlugin],

            //display
            initialView: "timeGridWeek",
            slotMinTime: '8:00:00',
            slotMaxTime: "22:00:00",
            allDaySlot: false,
            hiddenDays: [0,6],

            //selection
            selectable: true,
            select: this.select,

            //event
            events: [],
            eventSources: [this.state.events],
            eventClick: this.eventClick
        });

        calendar.render();

        this.setState({ calendar });
    }


    eventClick = info => {
        //this is the new implementation:
        console.log(this.state.calendar.view);
        this.calendarRef.current.render()
        return;

        const start = this.state.calendar.getOption("visibleRange");
        const vEvents = this.state.calendar.getEvents().filter(event => {
            return true;
        });
    };

    render() {
        return <div ref={this.calendarRef}>Calendar component</div>;
    }
}
