import FullCalendar from '@fullcalendar/react'
import timeGridPlugin from '@fullcalendar/timegrid'
import interactionPlugin from '@fullcalendar/interaction'
import React from 'react';

import userCourses from './ClassSearchBar'
import ClassSearchBar from "./ClassSearchBar";
import * as rosterdata from './roster.json'
import search_input from './ClassSearchBar'



export default class ReactCalendar extends React.Component {

    constructor() {
        super();
        this.state={
            events: [{
                "title": "MCB 150",
                "prof": "Helen",
                "rating": "5",
                "section": "L1",
                "CRN": "1237",
                "type": "Lecture",
                "daysOfWeek": [2,4],
                "startTime": "11:00",
                "slotDuration": "00:50"
            }]
        }
    }

    handleUserCoursesChange() {
        this.setState({
            events: [{
                "title": "CS 196",
                "prof": "Sami/Rohan",
                "rating": "6",
                "section": "L1",
                "CRN": "1239",
                "type": "Lecture",
                "daysOfWeek": [2,4],
                "startTime": "13:00",
                "slotDuration": "00:50"
            }]
        })
    }

   render() {
        return(
            <body>
            <FullCalendar
                plugins={[timeGridPlugin]}
                initialView={'timeGridWeek'}
                stickyHeaderDates={true}
                stickyFooterScrollbar={true}
                height={'auto'}
                contentHeight={925}
                editable={true}
                slotMinTime={'8:00:00'}
                slotMaxTime={"22:00:00"}
                hiddenDays={[0,6]}
                headerToolbar={false}
                allDaySlot={false}
                expandRows={true}
                handleWindowResize={true}
                eventDurationEditable={false}
                eventDragMinDistance={0}
                dayHeaderFormat={{'weekday':'long'}}
                eventStartEditable={true}
                durationEditable={false}



                //events={this.props.events}
                events={this.state.events}
                //eventContent={renderEventContent}
                //eventSources={[this.state.events]}
            />
            <button onClick={() => this.handleUserCoursesChange()}
                    >Refresh</button>
            </body>

        )


    }


}
