import React from 'react';
import Autocomplete from '@material-ui/lab/Autocomplete';
import TextField from "@material-ui/core/TextField";
import * as data from './roster.json';

import Fragment from 'render-fragment';

let search_input;

let userCourses;

export default class AddClasses extends React.Component {

    handleAddClick(search_input, userCourses) {
        if (userCourses == null) {
            userCourses = [search_input]
        } else {
            userCourses.concat(search_input)
        }
        console.log(userCourses)
    }


    render() {
        return(
            <Fragment>
                <Autocomplete
                    class="class_search_bar"
                    options={data.courses}
                    autoComplete={true}
                    onChange={(event, object) => {
                        //console.log(object)
                        search_input = object;
                    }}
                    getOptionLabel={(option) => option.title}
                    renderInput={(params) => <TextField {...params} variant="outlined"/>}
                />
                <button onClick={() => this.handleAddClick(search_input, userCourses)}> Add Class </button>
            </Fragment>

        )
    }
}
