import React, { Component } from "react";
//import RRule from "rrule";
import { Calendar } from "@fullcalendar/core";
import timeGridPlugin from "@fullcalendar/timegrid";
import FullCalendar from "@fullcalendar/react";
import search_input from "./ClassSearchBar";

let calendar
export default class CalendarDemo extends Component {

    constructor(props) {
        super(props);
        this.state = {
            calendar: null,
            events: this.props.events
        };
        this.calendarRef = React.createRef();
    }

    componentDidMount() {
         calendar = new Calendar(this.calendarRef.current, {
            //plugins
            plugins: [ timeGridPlugin],

            //display
            initialView: "timeGridWeek",
            slotMinTime: '8:00:00',
            slotMaxTime: "22:00:00",
            allDaySlot: false,
            hiddenDays: [0,6],

            //selection
            selectable: true,
            select: this.select,

            //event
            events: this.state.events,
            //eventSources: [this.state.events],
            eventClick: this.eventClick
        });

        calendar.render();

        this.setState({ calendar });
    }


    eventClick = info => {
            this.setState({
                events: this.state.events.concat(search_input)
            });
            calendar.render()
            console.log(this.state.events)
    };

    render() {
        return <div ref={this.calendarRef}>Calendar component</div>;
    }
}
